import React from "react";
import PropTypes from "prop-types";

const LabelInputItem = props => {
  const { labelText, selectValue, selectName, options } = props;

  return (
    <label className="profile-form__row">
      {labelText}
      <select
        defaultValue={selectValue}
        className="profile-form__field profile-form__select"
        name={selectName}
      >
        {generateOptions(options)}
      </select>
    </label>
  );
};

function generateOptions(options) {
  return options.map(option => (
    <option value={option.value}>{option.text}</option>
  ));
}

LabelInputItem.defaultProps = {
  labelText: "",
  selectValue: "",
  selectName: "",
  options: []
};

LabelInputItem.propTypes = {
  labelText: PropTypes.string,
  selectValue: PropTypes.string,
  selectName: PropTypes.string,
  options: PropTypes.arrayOf(
    PropTypes.shape({
      value: PropTypes.string,
      text: PropTypes.string
    })
  )
};

export default LabelInputItem;
